const menuButton=document.querySelector(".nav-panel-button"),mainNav=document.querySelector(".main-nav");function scrollToTop(){window.scroll({top:0,left:0,behavior:"smooth"})}menuButton.addEventListener("click",(()=>{mainNav.classList.toggle("main-nav-open"),menuButton.classList.toggle("nav-panel-button-open")})),window.addEventListener("scroll",(()=>{const o=document.querySelector(".cd-top");window.scrollY>600?o.style.opacity="1":o.style.opacity="0"})),window.addEventListener("scroll",(()=>{const o=document.querySelector(".header-logo");window.scrollY>100?o.classList.add("shrink"):o.classList.remove("shrink")}));